import React, { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import './chatbot.css';
import axios from 'axios';
import config from "../config";

const ChatbotUI = ({ chatID, chats }) => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState(chats[chatID] || []);
  const chatHistoryRef = useRef(null);
  const [isSending, setIsSending] = useState(false);
  const [isBotResponseComplete, setIsBotResponseComplete] = useState(false);
  const [polling, setPolling] = useState(false); // State to track polling status



  const handleViewPDFClick = async (pdfPath) => {
    if (!pdfPath) return;
    
    try {
      const response = await axios.get(
        `${config.API_BASE_URL}/get_highlighted_pdf?file_path=${encodeURIComponent(pdfPath)}`,
        {
          responseType: 'blob',
        }
      );
      
      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      
      // Open the PDF in a new tab
      window.open(url, "_blank");
      
      // Clean up the URL object after opening
      setTimeout(() => {
        window.URL.revokeObjectURL(url);
      }, 100);
    } catch (error) {
      if (error.response && error.response.status === 404) {
        alert("The highlighted PDF has expired. Please make the query again to generate a new highlight.");
      } else {
        console.error("Error fetching PDF:", error);
        alert("Error loading the PDF. Please try again.");
      }
    }
  };

  const handleSendMessage = async () => {
    if (input.trim() === "" || isSending) return;

    setIsSending(true);
    
    // Send the user's message
    const userMessage = { id: Date.now(), text: input, isBot: false };
    setMessages((prev) => [...prev, userMessage]);
    
    // Clear input field
    setInput("");
    
    try {
      // Send user's query to backend
      await axios.post(`${config.API_BASE_URL}/user_query`, {
        query: input,
        user_id: '1',
        session_id: chatID
      });
      
      // Start polling the backend for bot's response
      setPolling(true);

      // Fetch the bot's response continuously
      const intervalId = setInterval(async () => {
        try {
          // Fetch the chat session
          const response = await axios.get(`${config.API_BASE_URL}/get_chat_session?user_id=1&session_id=${chatID}`);
          const data = response.data;

          // Check if the bot has finished its response
          if (data && data.length > 0 && data[data.length - 1].agent_response) {
            // Format the response from the backend
            const botMessage = { 
              id: Date.now() + 1, 
              text: data[data.length - 1].agent_response, 
              isBot: true 
            };
            setMessages((prev) => [...prev, botMessage]);
            
            // Stop polling once the response is complete
            clearInterval(intervalId);
            setPolling(false);
          }
        } catch (error) {
          console.error("Error fetching chat session:", error);
        }
      }, 2000); // Poll every 2 seconds (adjust this interval as needed)

    } catch (error) {
      console.error("Error sending request:", error);
      setMessages((prev) => [
        ...prev,
        { id: Date.now() + 1, text: "Sorry, there was an issue processing your request.", isBot: true },
      ]);
    }

    // Set isSending to false after initiating the request
    setIsSending(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Scroll to the most recent message
  useEffect(() => {
    if (chatHistoryRef.current) {
      chatHistoryRef.current.scrollTop = chatHistoryRef.current.scrollHeight;
    }
  }, [messages]);

  useEffect(() => {
    setMessages(chats[chatID] || []);
  }, [chatID, chats]);



  return (
    <div className="chat-interaction-container">
      <div className="chat-container">
        <div className="chat-history" ref={chatHistoryRef}>
          {messages.length === 0 ? (
            <div className="no-messages">
              HOW CAN I HELP YOU
            </div>
          ) : (
            messages.map((msg) => (
              <div key={msg.id} className={`message ${msg.isBot ? 'bot' : 'user'}`}>
                <ReactMarkdown>{msg.text}</ReactMarkdown>
              </div>
            ))
          )}
        </div>
        <div className="input-container">
          <textarea
            className="text-input"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
            disabled={isSending}
          />
          <button className="send-button" onClick={handleSendMessage} disabled={isSending}>
            {isSending ? "Sending..." : "Send"}
          </button>
        </div>
        <div className="feedback-link">Give us feedback!</div>
      </div>
    </div>
  );


};

export default ChatbotUI;
