import React, { useState, useEffect } from 'react';
import './documentManagement.css';
import config from '../config'; // Ensure you have the API base URL configured

const DocumentManagement = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [documents, setDocuments] = useState([]);
  const [selectedDocument, setSelectedDocument] = useState(null);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch(`${config.API_BASE_URL}/get_documents`);
      const data = await response.json();
      setDocuments(data); 
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSelectDocument = (doc) => {
    setSelectedDocument(doc.id);
  };

  const handleViewDocument = async () => {
    if (!selectedDocument) return;

    try {
      const response = await fetch(`${config.API_BASE_URL}/get_pdf?file_id=${selectedDocument}`);
      if (!response.ok) throw new Error("Failed to fetch document");

      // Create a blob URL to open the PDF in a new tab
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      window.open(url, '_blank');
    } catch (error) {
      console.error('Error fetching PDF:', error);
    }
  };

  const filteredDocuments = documents.filter(doc =>
    doc.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="document-management-container">
      <div className="documents-container">
        <div className="input-container">
          <input
            type="text"
            placeholder="Search documents..."
            value={searchTerm}
            onChange={handleSearch}
            className="search-input"
          />
          <button className="view-button" onClick={fetchDocuments}>
            REFRESH
          </button>
          <button className="view-button" onClick={handleViewDocument} disabled={!selectedDocument}>
            VIEW
          </button>
        </div>

        <div className="document-list">
          {filteredDocuments.map(doc => (
            <div
              key={doc.id}
              className={`document-item ${selectedDocument === doc.id ? 'selected' : ''}`}
              onClick={() => handleSelectDocument(doc)}
            >
              {doc.name}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DocumentManagement;
