import React, { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import './chatbot.css';
import axios from 'axios';
import config from "../config";

const ChatbotUI = ({ chatID, chats, fetchUserSessions, createNewChatSession, setChats, setChatID }) => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState(chats[chatID] || []);
  const chatHistoryRef = useRef(null);
  const [isSending, setIsSending] = useState(false);

  const [selectedModel, setSelectedModel] = useState("GPT-4"); // Default model
  const models = ["Llama3.2:3b", "LLama3.3:7b", "deepseek-r1:1.5b"]; // get it from frontend

  const chatIdRef = useRef(chatID);

useEffect(() => {
  chatIdRef.current = chatID;
}, [chatID]);


const handleSendMessage = async () => {
  if (input.trim() === "" || isSending) return;

  setIsSending(true);

  let currentChatId = chatID;
  const userMessage = { id: Date.now(), text: input, isBot: false };

  // If chatID is null, create a new chat session
  if (!currentChatId) {
    try {
      currentChatId = await createNewChatSession();
      if (!currentChatId) {
        setMessages((prev) => [
          ...prev,
          { id: Date.now() + 1, text: "Error creating new chat session. Please try again.", isBot: true },
        ]);
        setIsSending(false);
        return;
      }
      
      // Ensure we don't overwrite messages with a new array if it already exists
      setChats(prev => ({
        ...prev,
        [currentChatId]: prev[currentChatId] || []  // Only set if it doesn't exist
      }));

      setChatID(currentChatId); // Update chat ID state immediately
    } catch (error) {
      console.error("Error creating new chat session:", error);
      setIsSending(false);
      return;
    }
  }

  // Add the user message **only once** (outside session creation logic)
  setMessages((prev) => [...prev, userMessage]);
  setChats(prev => ({
    ...prev,
    [currentChatId]: [...(prev[currentChatId] || []), userMessage]  // Ensure it appends correctly
  }));

  setInput("");

  try {
    const response = await axios.post(`${config.API_BASE_URL}/user_query`, {
      query: input,
      user_id: '1',
      session_id: currentChatId
    });

    const data = response.data;
    const botResponse = typeof data.response === 'string' ? data.response : JSON.stringify(data.response);
    const highlightedPdfPaths = data
  .filter(item => item.highlighted_pdf_path !== null) // Filter out items with null highlighted_pdf_path
  .map(item => ({
    highlighted_pdf_path: item.highlighted_pdf_path,
    filename: item.filename,
  }));

    const botMessageId = Date.now() + 1;
    const botMessage = { id: botMessageId, text: "", isBot: true, pdfPath: highlightedPdfPaths };

    // Append bot message
    setMessages((prev) => [...prev, botMessage]);
    setChats(prev => ({
      ...prev,
      [currentChatId]: [...prev[currentChatId], botMessage]
    }));

    // Simulate streaming effect
    const words = botResponse.split(" ");
    for (let i = 0; i < words.length; i++) {
      await new Promise((resolve) => setTimeout(resolve, 50));

      setMessages((prevMessages) =>
        prevMessages.map((msg) =>
          msg.id === botMessageId ? { ...msg, text: (msg.text || "") + words[i] + " " } : msg
        )
      );

      setChats(prev => ({
        ...prev,
        [currentChatId]: prev[currentChatId].map((msg) =>
          msg.id === botMessageId ? { ...msg, text: (msg.text || "") + words[i] + " " } : msg
        )
      }));
    }

  } catch (error) {
    console.error("Error sending request:", error);
    const errorMessage = { 
      id: Date.now() + 1, 
      text: "Sorry, there was an issue processing your request.", 
      isBot: true 
    };
    setMessages((prev) => [...prev, errorMessage]);
    setChats(prev => ({
      ...prev,
      [currentChatId]: [...prev[currentChatId], errorMessage]
    }));
  }
  setIsSending(false);
};



  const handleViewPDFClick = async (pdfPath) => {
    if (!pdfPath) return;
    
    try {
      const response = await axios.get(
        `${config.API_BASE_URL}/get_highlighted_pdf?file_path=${encodeURIComponent(pdfPath)}`,
        {
          responseType: 'blob',
        }
      );
      
      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      
      // Open the PDF in a new tab
      window.open(url, "_blank");
      
      // Clean up the URL object after opening
      setTimeout(() => {
        window.URL.revokeObjectURL(url);
      }, 100);
    } catch (error) {
      if (error.response && error.response.status === 404) {
        alert("The highlighted PDF has expired. Please make the query again to generate a new highlight.");
      } else {
        console.error("Error fetching PDF:", error);
        alert("Error loading the PDF. Please try again.");
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Scroll to the most recent message
  useEffect(() => {
    if (chatHistoryRef.current) {
      chatHistoryRef.current.scrollTop = chatHistoryRef.current.scrollHeight;
    }
  }, [messages]);

  useEffect(() => {
    setMessages(chats[chatID] || []);
  }, [chatID, chats]);



  return (
    <div className="chat-interaction-container">
      <div className="chat-container">

      <div className="chat-header">
      <select
        className="model-select"
        value={selectedModel}
        onChange={(e) => setSelectedModel(e.target.value)}
      >
        {models.map((model) => (
          <option key={model} value={model}>
            {model}
          </option>
        ))}
      </select>
          You are speaking with ✨ {selectedModel} ✨
      </div>
      <div className="chat-history" ref={chatHistoryRef}>
  {messages.length === 0 ? (
    <div className="message bot">
      <ReactMarkdown>
        Hello! Let me know what you're curious about, and I'll find the relevant documents for you. How can I help you?
      </ReactMarkdown>
    </div>
  ) : (
    messages.map((msg) => (
      <div key={msg.id} className={`message ${msg.isBot ? 'bot' : 'user'}`}>
        <ReactMarkdown>{msg.text}</ReactMarkdown>

        {/* If it's a bot message and contains PDF paths */}
        {msg.isBot && msg.pdfPath && msg.pdfPath.length > 0 && (
          <div>
            {/* Iterate through the PDF paths and filenames */}
            {msg.pdfPath.map((pdf, index) => (
              <button
                key={index}
                className="display-button"
                onClick={() => handleViewPDFClick(pdf.highlighted_pdf_path)}
              >
                <div className="pdfLabel">{pdf.filename}</div>
              </button>
            ))}
          </div>
        )}
      </div>
    ))
  )}
</div>
        <div className="input-container">
          <textarea
            className="text-input"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
            disabled={isSending}
          />
          <button className="send-button" onClick={handleSendMessage} disabled={isSending}>
            {isSending ? "Waiting..." : "Send"}
          </button>
        </div>
        <div className="feedback-link">
      <a href="https://docs.google.com/forms/d/e/1FAIpQLSedQob3XPLOoiyA3sLy7jsVG0L3twcE_upSVL7ezV7NSuSVYQ/viewform?usp=header" target="_blank" rel="noopener noreferrer">
        Give us feedback!
      </a>
</div>

      </div>
    </div>
  );
  


};

export default ChatbotUI;
