import React, { useState, useEffect } from 'react';
import ChatbotUI from './chatbot';
import SidePanel from './sidepanel';
import './Main.css';

const Main = () => {
  const [chatID, setChatID] = useState("NONE"); // Shared state for selected chat
  const [sessions, setSessions] = useState([]);
  const [chats, setChats] = useState({}); // State to store chats

  const fetchUserSessions = async () => {
    try {
      const response = await fetch('http://localhost:5000/get_user_sessions?user_id=1');
      const data = await response.json();
      if (response.ok) {
        // Transform the data into a chat object that maps session_id to messages
        const chatData = data.reduce((acc, session) => {
          acc[session.session_id] = session.messages || [];
          return acc;
        }, {});
        setSessions(data);  // Keep the original session data for SidePanel
        setChats(chatData); // Set chats with the new structure
      } else {
        console.error('Error fetching sessions:', data.error);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };


  useEffect(() => {
    fetchUserSessions();
  }, []); // Fetch user sessions on mount

  return (
    <div className="main-container">
      <div className="left-panel">
        <SidePanel chatID={chatID} setChatID={setChatID} sessions={sessions} />
      </div>
      <div className="right-panel">
        <ChatbotUI chatID={chatID} chats={chats} />
      </div>
    </div>
  );
};

export default Main;
