import React, { useState, useEffect } from 'react';
import './documentManagement.css';
import config from "../config";

const DocumentManagement = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [documents, setDocuments] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [documentToDelete, setDocumentToDelete] = useState(null);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch(`${config.API_BASE_URL}/get_documents`);
      const data = await response.json();

      if (response.ok) {
        setDocuments(data);
      } else {
        console.error('Error fetching documents:', data.error);
      }
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  const viewDocument = async (documentId) => {
    try {
      const response = await fetch(`${config.API_BASE_URL}/get_pdf?file_id=${documentId}`);
      if (response.ok) {
        const blob = await response.blob();
        const url = URL.createObjectURL(blob);
        window.open(url, '_blank');
      } else {
        console.error('Error fetching PDF:', await response.json());
      }
    } catch (error) {
      console.error('Error fetching PDF:', error);
    }
  };

  const handleDeleteClick = (documentId) => {
    setDocumentToDelete(documentId);
    setIsModalOpen(true);
  };

  const deleteDocument = async () => {
    if (!documentToDelete) {
      console.error('No document selected for deletion!');
      return;
    }
  
    try {
      const response = await fetch(`${config.API_BASE_URL}/delete_document?file_id=${documentToDelete}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
  
      const data = await response.json();
  
      if (response.ok) {
        setDocuments(documents.filter(doc => doc.id !== documentToDelete));
        setDocumentToDelete(null);
        console.log('Document deleted:', data.message);
      } else {
        console.error('Error deleting document:', data.error);
      }
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  
    setIsModalOpen(false);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredDocuments = documents.filter(doc =>
    doc.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="document-management-container">
      <div className="documents-container">
        <div className="input-container">
          <input
            type="text"
            placeholder="Search documents..."
            value={searchTerm}
            onChange={handleSearch}
            className="search-input"
          />
          <button className="refresh-button" onClick={fetchDocuments}>
            REFRESH
          </button>
        </div>
  
        <div className="document-list">
          {filteredDocuments.length > 0 ? (
            filteredDocuments.map(doc => (
              <div
                key={doc.id}
                className="document-item-container"
              >
                <div className="document-name">{doc.name}</div>
                <div className="document-actions">
                  <button
                    className="view-button"
                    onClick={() => viewDocument(doc.id)}
                  >
                    VIEW
                  </button>
                  <button
                    className="delete-button"
                    onClick={() => handleDeleteClick(doc.id)}
                  >
                    DELETE
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div className="no-documents">No documents found</div>
          )}
        </div>
      </div>
  
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Confirm Deletion</h3>
            <p>Are you sure you want to delete this document?</p>
            <div className="modal-buttons">
              <button className="confirm-button" onClick={deleteDocument}>
                Yes, Delete
              </button>
              <button className="cancel-button" onClick={() => setIsModalOpen(false)}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DocumentManagement;
