import React, { useState, useEffect } from 'react';
import ChatbotUI from './chatbot';
import SidePanel from './sidepanel';
import './Main.css';

const Main = () => {
  const [chatID, setChatID] = useState("NONE"); // Shared state for selected chat
  const [sessions, setSessions] = useState([]);

  const fetchUserSessions = async () => {
    try {
      // TODO: user id degisecek
      const response = await fetch('http://localhost:5000/get_user_sessions?user_id=1');
      const data = await response.json();
      if (response.ok) {
        setSessions(data);  // Set the sessions data for rendering in SidePanel
      } else {
        console.error('Error fetching sessions:', data.error);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };



  useEffect(() => {
    fetchUserSessions();
  }, []); // Fetch user sessions on mount

  return (
    <div className="main-container">
      <div className="left-panel">
        <SidePanel chatID={chatID} setChatID={setChatID} sessions={sessions} />
      </div>
      <div className="right-panel">
        <ChatbotUI chatID={chatID} />
      </div>
    </div>
  );
};

export default Main;
