import React, { useState } from "react";
import "./chatbot.css";

const ChatbotUI = () => {
  const [messages, setMessages] = useState([
    { id: 1, text: "Hello! How can I assist you today?", isBot: true },
  ]);
  const [input, setInput] = useState("");
  const [isDimVisible, setIsDimVisible] = useState(false); // State for dim overlay visibility
  const [isPdfVisible, setIsPdfVisible] = useState(false); // State for PDF visibility
  const [isLoading, setIsLoading] = useState(false); // State for showing loading indicator

  const handleSendMessage = async () => {
    if (input.trim() === "") return;

    const userMessage = { id: Date.now(), text: input, isBot: false };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");
    setIsLoading(true);

    try {
      const response = await fetch("http://127.0.0.1:5000/user_query", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ query: input }), // Send the input as the user query
      });

      // Ensure response body is available for streaming
      if (!response.body) throw new Error("No response body");

      const reader = response.body.getReader();
      const decoder = new TextDecoder("utf-8");
      let botMessage = "";

      // Read the stream progressively
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        // Decode and append the streamed chunk
        botMessage += decoder.decode(value, { stream: true });
        setMessages((prev) => {
          const lastBotMessage = prev.find(
            (msg) => msg.isBot && msg.id === "streaming"
          );

          // Update the streaming message or create a new one
          if (lastBotMessage) {
            return prev.map((msg) =>
              msg.id === "streaming"
                ? { ...msg, text: botMessage.trim() }
                : msg
            );
          } else {
            return [
              ...prev,
              { id: "streaming", text: botMessage.trim(), isBot: true },
            ];
          }
        });
      }

      // Finalize the bot's message
      setMessages((prev) =>
        prev.map((msg) =>
          msg.id === "streaming"
            ? { ...msg, id: Date.now(), text: botMessage.trim() }
            : msg
        )
      );
    } catch (error) {
      console.error("Error streaming response:", error);
      setMessages((prev) => [
        ...prev,
        {
          id: Date.now(),
          text: "Sorry, there was an issue processing your request.",
          isBot: true,
        },
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleViewPDFClick = () => {
    setIsDimVisible(true); // Show dim overlay when the button is clicked
    setIsPdfVisible(true); // Show PDF viewer
  };

  const handleBackArrowClick = () => {
    setIsDimVisible(false); // Hide dim overlay when back arrow is clicked
    setIsPdfVisible(false); // Hide PDF viewer
  };

  return (
    <div className="app-container">
      {isDimVisible && (
        <div className="dim">
          {/* Back arrow button to restore lights */}
          <button className="back-arrow-button" onClick={handleBackArrowClick}>
            ‚Üê
          </button>
        </div>
      )}

      {/* PDF Viewer */}
      {isPdfVisible && (
        <div className="pdf-container">
          {/* Using iframe to display the PDF */}
          <iframe
            src="/pdfs/aaa.pdf" // Assuming the PDF is in the public folder
            title="PDF Viewer"
          ></iframe>
        </div>
      )}

      <div className="chat-container">
        <div className="chat-header">SUDoc</div>

        <div className="chat-history">
          {messages.map((msg) => (
            <div
              key={msg.id}
              className={`message ${msg.isBot ? "bot" : "user"}`}
            >
              {msg.text}

              {/* Show the button below the chatbot message only */}
              {msg.isBot && (
                <button className="display-button" onClick={handleViewPDFClick}>
                  View PDF
                </button>
              )}
            </div>
          ))}
          {isLoading && <div className="message bot">Typing...</div>}
        </div>

        <div className="input-container">
          <input
            type="text"
            className="text-input"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
          />
          <button className="send-button" onClick={handleSendMessage}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatbotUI;
