import React, { useState, useEffect } from 'react';
import './sidepanel.css';
import Settings from './side-panel-options/sidepanelSettings'; // Import the Settings component
import FileUpload from './side-panel-options/sidepanelupload'; // Import the FileUpload component
import SUlogo from '../assets/Sabanci_University_logo.png';
import Search from './side-panel-options/sidepanelsearch'; // Import the Search component
import DeleteIcon from "../assets/chat-circle-remove-svgrepo-com.svg"
import CreateIcon from "../assets/chat-circle-add-svgrepo-com.svg"
import ChatsIcon from "../assets/chat-conversation-circle-svgrepo-com.svg"
import SettingsIcon from "../assets/settings-svgrepo-com.svg"
import UploadIcon from "../assets/upload-file-2-svgrepo-com.svg"

const SidePanel = ({ chatID, setChatID }) => {
  const [selectedOption, setSelectedOption] = useState(0);
  const [sessions, setSessions] = useState([]); // State to hold the list of chat sessions
  const options = ["Chats", "Upload a document", "Settings"];
  const optionIcons = [ChatsIcon, SettingsIcon, UploadIcon];

  // Fetch all user sessions when the component mounts
  useEffect(() => {
    const fetchUserSessions = async () => {
      try {
        // TODO: user id degisecek
        const response = await fetch('http://localhost:5000/get_user_sessions?user_id=1'); // Replace with dynamic user_id if needed
        const data = await response.json();
        if (response.ok) {
          setSessions(data); // Set the sessions data for rendering in SidePanel
        } else {
          console.error('Error fetching sessions:', data.error);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUserSessions();
  }, []);

  // Function to create a new chat session
  const createNewChat = async () => {
    try {
      const response = await fetch('http://localhost:5000/create_chat_session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ user_id: '1' }) // Replace with dynamic user_id if needed
      });

      const data = await response.json();
      if (response.ok) {
        console.log('New chat session created:', data);
        setChatID(data.session_id);  // Set the chatID to the new session's ID
        setSessions(prevSessions => [...prevSessions, { session_id: data.session_id, created_at: new Date() }]); // Add the new session to the sessions list
      } else {
        console.error('Error creating chat session:', data.error);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Render different content based on the selected option
  const renderListingContent = () => {
    switch (selectedOption) {
      case 0: // Chats
        return (
          <div className="side-panel-listing">
            <div className="side-panel-listing-createchat" onClick={createNewChat}>
              <div>New Chat</div>
              <img src={CreateIcon} alt="createicon" style={{ width: '20px', height: 'auto' }} />
            </div>
            {sessions.map(session => (
              <div key={session.session_id} className="side-panel-listing-element" onClick={() => setChatID(session.session_id)}>
                <div className="side-panel-listing-element-subtitle-container">
                  <div>{session.session_id}</div>
                  <img src={DeleteIcon} alt="deleteicon" style={{ width: '20px', height: 'auto' }} />
                </div>
                <div className="side-panel-listing-element-references">
                  <div className="side-panel-listing-element-reference">Document ref</div>
                  <div className="side-panel-listing-element-reference">Document ref2</div>
                </div>
              </div>
            ))}
          </div>
        );

      case 1: // Upload a document
        return <FileUpload />;

      case 2: // Settings
        return <Settings />;

      default:
        return <div className="side-panel-listing">No content available for this option.</div>;
    }
  };

  return (
    <div className="side-panel">
      <div className="side-panel-upper">
        <div className="side-panel-title">
          <div className="logo-container">
            <img src={SUlogo} alt="SUlogo" style={{ width: '150px', height: 'auto' }} />
          </div>
          <div className="side-panel-title-text">Document Management System</div>
        </div>
      </div>

      <div className="side-panel-lower">
        <div className="side-panel-listing-title">{options[selectedOption]}</div>
        {renderListingContent()}
      </div>

      <div className="side-panel-bottom">
        <div className="side-panel-options">
          {options.map((option, index) => (
            <div
              key={index}
              className={`side-panel-options-button ${selectedOption === index ? 'active' : ''}`}
              onClick={() => setSelectedOption(index)}
            >
              <img src={optionIcons[index]} alt="icon" style={{ width: '20px', height: 'auto' }} />
              <div>{option}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SidePanel;
